import React, { useState, useEffect } from 'react';
import api from '../utils/api';

const Carpool = () => {
  const [carpools, setCarpools] = useState([]);
  const [formData, setFormData] = useState({
    event: '',
    date: '',
    startLocation: '',
    endLocation: '',
  });

  const { event, date, startLocation, endLocation } = formData;

  useEffect(() => {
    // Fetch existing carpools
    const fetchCarpools = async () => {
      try {
        const token = localStorage.getItem('token');
        const response = await api.get('/api/carpool', {
          headers: {
            'x-auth-token': token,
          },
        });
        setCarpools(response.data);
      } catch (err) {
        console.error('Error fetching carpools:', err);
      }
    };

    fetchCarpools();
  }, []);

  const onChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

  const onSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      await api.post('/api/carpool/create', formData, {
        headers: {
          'x-auth-token': token,
        },
      });
      // Refresh the list of carpools
      window.location.reload();
    } catch (err) {
      console.error('Error creating carpool:', err);
    }
  };

  const joinCarpool = async (carpoolId) => {
    try {
      const token = localStorage.getItem('token');
      await api.put(`/api/carpool/join/${carpoolId}`, {}, {
        headers: {
          'x-auth-token': token,
        },
      });
      // Refresh the list of carpools
      window.location.reload();
    } catch (err) {
      console.error('Error joining carpool:', err);
    }
  };

  const leaveCarpool = async (carpoolId) => {
    try {
      const token = localStorage.getItem('token');
      await api.put(`/api/carpool/leave/${carpoolId}`, {}, {
        headers: {
          'x-auth-token': token,
        },
      });
      // Refresh the list of carpools
      window.location.reload();
    } catch (err) {
      console.error('Error leaving carpool:', err);
    }
  };

  return (
    <div>
      <h2>Create a Carpool</h2>
      <form onSubmit={onSubmit}>
        <div>
          <label>Event:</label>
          <input type="text" name="event" value={event} onChange={onChange} required />
        </div>
        <div>
          <label>Date:</label>
          <input type="date" name="date" value={date} onChange={onChange} required />
        </div>
        <div>
          <label>Start Location:</label>
          <input type="text" name="startLocation" value={startLocation} onChange={onChange} required />
        </div>
        <div>
          <label>End Location:</label>
          <input type="text" name="endLocation" value={endLocation} onChange={onChange} required />
        </div>
        <button type="submit">Create Carpool</button>
      </form>

      <h2>Available Carpools</h2>
      {carpools.length > 0 ? (
        <ul>
          {carpools.map((carpool) => {
            // Console logging to debug the issue
            console.log("Current Carpool:", carpool);
            console.log("Current User ID:", localStorage.getItem('user_id'));

            return (
              <li key={carpool._id}>
                <strong>{carpool.event}</strong> on {new Date(carpool.date).toLocaleDateString()} from {carpool.startLocation} to {carpool.endLocation}
                <br />
                Driver: {carpool.driver?.name || 'Unknown'}
                <br />
                Passengers: {carpool.passengers.length > 0
                  ? carpool.passengers.map((passenger) => passenger.name).join(', ')
                  : 'No passengers yet'}
                <br />
                {/* Display Leave Carpool button if the current user is a passenger */}
                {carpool.passengers.some((passenger) => {
                  console.log("Passenger ID:", passenger._id, "Match:", passenger._id === localStorage.getItem('user_id'));
                  return passenger._id === localStorage.getItem('user_id');
                }) ? (
                  <button onClick={() => leaveCarpool(carpool._id)}>Leave Carpool</button>
                ) : carpool.driver._id !== localStorage.getItem('user_id') ? (
                  <button onClick={() => joinCarpool(carpool._id)}>Join Carpool</button>
                ) : null}
              </li>
            );
          })}
        </ul>
      ) : (
        <p>No carpools available.</p>
      )}
    </div>
  );
};

export default Carpool;
